package blockchain;

import java.security.MessageDigest;

class StringUtil {
    /* Applies Sha256 to a string and returns a hash. */
    public static String applySha256(String input){
        try {
            MessageDigest digest = MessageDigest.getInstance("SHA-256");
            /* Applies sha256 to our input */
            byte[] hash = digest.digest(input.getBytes("UTF-8"));
            StringBuilder hexString = new StringBuilder();
            for (byte elem: hash) {
                String hex = Integer.toHexString(0xff & elem);
                if(hex.length() == 1) hexString.append('0');
                hexString.append(hex);
            }
            return hexString.toString();
        }
        catch(Exception e) {
            throw new RuntimeException(e);
        }
    }
}

package blockchain;

import java.util.Date;
import java.util.LinkedList;

class Blockchain {

    LinkedList<Block> blocks = new LinkedList<>();
    private int lastId = 0;

    void generate() {

        String lastHash = "0";
        if (blocks.size() > 0) {
            lastHash = blocks.getLast().blockHash;
        }

        Block block = new Block(++this.lastId, lastHash);

        block.add("data", "Super secure Data");

        this.blocks.add(block);
    }

    void print (int from, int to) throws IndexOutOfBoundsException {
        if (from > blocks.size() || to > blocks.size() || from > to) {
            throw new IndexOutOfBoundsException();
        }
        for (int i = from; i < to; i++) {
            Block b = blocks.get(i);
            System.out.println(b);
            System.out.println();
        }
    }

    private boolean isValid() {
        String lastPrevHash = "";
        String lastBlockHash = "";
        boolean valid = true;
        for (Block b : blocks) {
            if ("".equals(lastPrevHash)) {
                lastPrevHash = "0";
                lastBlockHash = b.sha256();
            } else {
                valid = valid && lastBlockHash.equals(b.prevHash);
                //System.out.println(lastBlockHash);
                //System.out.println(b.prevHash);
                lastBlockHash = b.sha256();
                lastPrevHash = b.prevHash;
            }
        }
        return valid;
    }

    void validate() {
        if (this.isValid()) {
            System.out.println("Chain is valid!");
        } else {
            System.out.println("Chain is NOT valid!");
        }
    }
}

package blockchain;

import java.util.Date;
import java.util.HashMap;

public class Block {

    final String prevHash;
    final private int id;
    HashMap<String, String> information = new HashMap<>();
    String blockHash;

    Block(int id, String prevHash) {
        this.id = id;
        this.prevHash = prevHash;
        this.add("timestamp", String.valueOf(new Date().getTime()));
        this.hash();
    }

    void add(String key, String information) {
        this.information.put(key, information);
        this.hash();
    }

    String sha256() {
        return StringUtil.applySha256(this.prevHash + this.id + this.information.toString());
    }

    private void hash() {
        this.blockHash = this.sha256();
    }

    @Override
    public String toString() {

        return "Block:\n" +
                String.format("Id: %d\n", this.id) +
                String.format("Timestamp: %s\n", this.information.get("timestamp")) +
                "Hash of the previous block:\n" +
                this.prevHash + "\n" +
                "Hash of the block:\n" +
                this.blockHash;
    }
}

